			if ( !( GlobalVars.going != 0 ) ) {
				GlobalVars.going = 2;
				GlobalVars.ticker.pregame_timeleft = Game13.timeofday + GlobalVars.ticker.remaining_time;
				GlobalFuncs.to_chat( typeof(Game13), "<b>The game will start soon.</b>" );
