		public bool makeNukeTeam(  ) {
			GameMode_Nuclear temp = null;
			ByTable candidates = null;
			ByTable chosen = null;
			dynamic theghost = null;
			int? numagents = null;
			int agentcount = 0;
			int? i = null;
			dynamic j = null;
			string nuke_code = null;
			dynamic nuke = null;
			ByTable synd_spawn = null;
			Obj_Effect_Landmark A = null;
