			if ( candidates.len != 0 ) {
				numagents = 5;
				agentcount = 0;
				i = null;
				i = 0;

				while (( i ??0) < ( numagents ??0)) {
					GlobalFuncs.shuffle( candidates );

					foreach (dynamic _a in Lang13.Enumerate( candidates )) {
						j = _a;
						

						if ( !Lang13.Bool( j ) || !Lang13.Bool( j.client ) ) {
							candidates.Remove( j );
							continue;
						}
						theghost = j;
						candidates.Remove( theghost );
						chosen.Add( theghost );
						agentcount++;
						break;
					}
					i++;
				}

				if ( agentcount < 3 ) {
					return false;
				}
